package ws_java;

public class CharTest {

  public static void main(String[] args) {
    // TODO Auto-generated method stub
    char c1 = '대';
    char c2 = '한';
    char c3 = '민';
    char c4 = '국';
    
    System.out.println(c1);
    System.out.println(c2);
    System.out.println(c3);
    System.out.println(c4);
    
    c1 = 'A';
    System.out.println("평가: " + c1);
  }

}
