package ws_java;

public class RealTest {

  public static void main(String[] args) {
    // TODO Auto-generated method stub
    System.out.println("100의 100%: " + 100 * 1.0);
    System.out.println("100의 50%: " + 100 * 0.5);
    System.out.println("100의 10%: " + 100 * 0.1);
    System.out.println("100의 1%: " + 100 * 0.01);
    System.out.println("100의 1.7%: " + 100 * 0.017);
    System.out.println("100의 1.75%: " + 100 * 0.0175);
    System.out.println("----------------------------------");
    System.out.println("100의 100%: " + (int)(100 * 1.0));
    System.out.println("100의 50%: " + (int)(100 * 0.5));
    System.out.println("100의 10%: " + (int)(100 * 0.1));
    System.out.println("100의 1%: " + (int)(100 * 0.01));
    System.out.println("100의 1.7%: " + (int)(100 * 0.017));
    System.out.println("100의 1.75%: " + (int)(100 * 0.0175));
  }

}
